mapper.update:
update tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId}
where shop_id=#{shopId}

